let productionData = msg.production_data;
let downtimeData = msg.downtime_data;
let line = flow.get('line')
let shiftDuration = flow.get('shiftDuration24h') * 3600

let groupedData = {};

productionData.forEach(entry => {
    if (entry.line === line) {
        let date = entry.timestamp.split(" ")[0];

        if (!groupedData[date]) {
            groupedData[date] = {
                totalShiftDuration: shiftDuration,
                totalGoodUnits: 0,
                totalProducedUnits: 0,
                totalDowntimeSeconds: 0,
                totalCycleTime: 0,
                cycleCount: 0,
                totalTargetOutput: 0,
                timestamp: entry.timestamp // Store full timestamp for sorting
            };
        }

        groupedData[date].totalGoodUnits += entry.good_units;
        groupedData[date].totalProducedUnits += entry.total_produced_units;
        groupedData[date].totalCycleTime += entry.cycle_time;
        groupedData[date].cycleCount++;
        groupedData[date].totalTargetOutput += entry.target_output;
    }
});

downtimeData.forEach(downtime => {
    if (downtime.line === line) {
        let date = downtime.timestamp.split(" ")[0];
        if (groupedData[date]) {
            groupedData[date].totalDowntimeSeconds += downtime.downtime_duration_minutes * 60;
        }
    }
});

let oeeResults = Object.entries(groupedData).map(([date, data]) => {
    let avgCycleTime = data.cycleCount > 0 ? data.totalCycleTime / data.cycleCount : 0;
    let availableTime = data.totalShiftDuration - data.totalDowntimeSeconds;
    let availability = availableTime / data.totalShiftDuration;
    let performance = data.totalTargetOutput > 0 ? data.totalProducedUnits / data.totalTargetOutput : 0;
    let quality = data.totalProducedUnits > 0 ? data.totalGoodUnits / data.totalProducedUnits : 0;
    let oee = (availability * performance * quality * 100).toFixed(2);

    return { date, availability, performance, quality, oee, timestamp: data.timestamp };
});

// Sort data by timestamp (oldest to most recent)
oeeResults.sort((a, b) => new Date(a.timestamp).valueOf() - new Date(b.timestamp).valueOf());
msg.payload = oeeResults;
return msg;

function calculateDowntimeByReason(downtimeData) {
    if (!Array.isArray(downtimeData) || downtimeData.length === 0) {
        return [];  // Ensure it's an array and return [] if empty or invalid
    }

    const summary = {};

    downtimeData.forEach(({ downtime_reason, downtime_duration_minutes }) => {
        summary[downtime_reason] = (summary[downtime_reason] || 0) + downtime_duration_minutes;
    });

    return Object.entries(summary).map(([reason, duration]) => ({
        downtime_reason: reason,
        downtime_duration_minutes: duration
    }));
}

// Example usage
msg.payload = calculateDowntimeByReason(msg.payload) || [];
return msg;

const productionData = msg.production_data;
const downtimeEvents = msg.downtime_data;
const shiftDuration = (flow.get('shift_duration') || 1) * 60; // Convert hours to minutes

// Group production data by machine (including area)
let machineData = {};
productionData.forEach(data => {
    if (!machineData[data.machine_name]) {
        machineData[data.machine_name] = {
            total_produced_units: 0,
            good_units: 0,
            target_output: 0,
            count: 0,
            area: data.area // Store area
        };
    }
    machineData[data.machine_name].total_produced_units += data.total_produced_units;
    machineData[data.machine_name].good_units += data.good_units;
    machineData[data.machine_name].target_output += data.target_output;
    machineData[data.machine_name].count += 1;
});

let oeeResults = Object.keys(machineData).map(machineName => {
    let data = machineData[machineName];

    let machineDowntime = downtimeEvents.filter(event => event.machine_name === machineName);

    function calculateOEE(data, downtime) {
        if (data.target_output === 0) {
            return { availability: 0, performance: 0, quality: 0, oee: 0 };
        }

        let totalDowntime = downtime.reduce((acc, event) =>
            typeof event.downtime_duration_minutes === 'number' ? acc + event.downtime_duration_minutes : acc
            , 0);

        let availability = (shiftDuration - totalDowntime) / shiftDuration;
        availability = Math.max(0, Math.min(1, availability));

        let performance = data.target_output > 0 ? data.total_produced_units / data.target_output : 0;
        let quality = data.total_produced_units > 0 ? data.good_units / data.total_produced_units : 0;

        let oee = availability * performance * quality;

        return {
            availability: parseFloat((availability * 100).toFixed(2)),
            performance: parseFloat((performance * 100).toFixed(2)),
            quality: parseFloat((quality * 100).toFixed(2)),
            oee: parseFloat((oee * 100).toFixed(2))
        };
    }

    let metrics = calculateOEE(data, machineDowntime);

    return {
        machine_name: machineName,
        area: data.area,
        oee: metrics.oee
    };
});

// Filter only machines with OEE < 85
msg.payload = oeeResults.filter(machine => machine.oee < 85);

return msg;

function generateProductionData() {
    const areas = {
        "Pressing": ["Hydraulic Press", "CNC Press Brake", "Stamping Press", "Power Press"],
        "Assembly": ["Robotic Arm", "Screw Insertion Machine", "Pick-and-Place Machine"],
        "Packaging": ["Carton Sealing Machine", "Shrink Wrapping Machine", "Bottle Filling and Capping Machine", "Flow Wrapper"]
    };

    const shifts = ["Shift 1", "Shift 2", "Shift 3"];
    const shiftStartTimes = {
        "Shift 1": "00:00:00",
        "Shift 2": "08:00:00",
        "Shift 3": "16:00:00"
    };

    const uniqueShortCodes = {
        "Pressing": ["HP", "PB", "SP", "PP"],
        "Assembly": ["RA", "SIM", "P and P"],
        "Packaging": ["CSM", "SWM", "BFCM", "FW"]
    };

    const downtimeTypes = ["Planned", "Unplanned"];
    const plannedDowntimeReasons = [
        "Scheduled Maintenance", "Equipment Upgrades", "Shift Changeover",
        "Tool Changeover", "Calibration and Quality Checks", "Cleaning and Sanitation", "Planned Power Outage"
    ];
    const unplannedDowntimeReasons = [
        "Power Failure", "Material Shortage", "Technical Fault", "Operator Unavailable"
    ];

    const data = [];
    const downtimeData = [];
    const now = new Date();

    const normalizeTimestamp = (timestamp) => timestamp.toISOString().split("T").join(" ").slice(0, 19);

    for (let i = 0; i < 30; i++) {
        const date = new Date();
        date.setDate(now.getDate() - i);
        const formattedDate = date.toISOString().split("T")[0];

        shifts.forEach(shift => {
            const shiftStart = shiftStartTimes[shift];
            const shiftTimestamp = new Date(`${formattedDate}T${shiftStart}`);

            Object.entries(areas).forEach(([area, machines]) => {
                machines.forEach((_, index) => {
                    const randomCode = uniqueShortCodes[area][Math.floor(Math.random() * uniqueShortCodes[area].length)];
                    const uniqueID = Math.floor(10000 + Math.random() * 90000);
                    const shortMachineName = `${randomCode}-${uniqueID}`;

                    const shiftDuration = 8;
                    let downtimeMinutes = 0;
                    if (Math.random() < 0.25) {
                        downtimeMinutes = Math.floor(shiftDuration * 60 * Math.random() * 0.2);
                    }
                    const operatingTime = (shiftDuration * 60 - downtimeMinutes) * 60;

                    let efficiency = Math.random() * 0.2 + 0.75;
                    let targetOutput = Math.floor(operatingTime / 3) || 1;
                    let totalProduced = Math.floor(targetOutput * efficiency);
                    totalProduced = Math.min(totalProduced, targetOutput);

                    const defectRate = Math.random() * 0.02 + 0.01;
                    const defectUnits = Math.floor(totalProduced * defectRate);
                    const goodUnits = totalProduced - defectUnits;

                    const line = `Line-${index + 1}`;
                    const timestamp = normalizeTimestamp(shiftTimestamp);

                    if (downtimeMinutes > 0) {
                        const downtimeStartMinutes = Math.floor(Math.random() * (shiftDuration * 60 - downtimeMinutes));
                        const downtimeStart = new Date(shiftTimestamp);
                        downtimeStart.setMinutes(downtimeStart.getMinutes() + downtimeStartMinutes);

                        const downtimeEnd = new Date(downtimeStart);
                        downtimeEnd.setMinutes(downtimeEnd.getMinutes() + downtimeMinutes);

                        const downtimeType = downtimeTypes[Math.floor(Math.random() * downtimeTypes.length)];
                        const downtimeReason = downtimeType === "Planned"
                            ? plannedDowntimeReasons[Math.floor(Math.random() * plannedDowntimeReasons.length)]
                            : unplannedDowntimeReasons[Math.floor(Math.random() * unplannedDowntimeReasons.length)];

                        downtimeData.push({
                            timestamp,
                            area,
                            machine_name: shortMachineName,
                            shift,
                            downtime_start: normalizeTimestamp(downtimeStart),
                            downtime_end: normalizeTimestamp(downtimeEnd),
                            downtime_duration_minutes: downtimeMinutes,
                            downtime_reason: downtimeReason,
                            downtime_type: downtimeType,
                            line
                        });
                    }

                    data.push({
                        timestamp,
                        area,
                        machine_name: shortMachineName,
                        shift,
                        shift_duration: shiftDuration,
                        operating_time: operatingTime - downtimeMinutes * 60,
                        good_units: goodUnits,
                        defect_units: defectUnits,
                        total_produced_units: totalProduced,
                        target_output: targetOutput,
                        line
                    });
                });
            });
        });
    }

    return [
        { payload: data },
        { payload: downtimeData }
    ];
}

const productionDataSet = generateProductionData();

return [
    { payload: productionDataSet[0].payload },
    { payload: productionDataSet[1].payload }
];

